server/index.ts.content:
import 'dotenv/config';
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

declare module 'http' {
  interface IncomingMessage { rawBody: unknown }
}

app.use(express.json({
  verify: (req, _res, buf) => { req.rawBody = buf; }
}));
app.use(express.urlencoded({ extended: false }));

// Middleware de logging
app.use((req, res, next) => {
  const start = Date.now();
  let capturedJsonResponse: Record<string, any> | undefined;
  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };
  res.on("finish", () => {
    const duration = Date.now() - start;
    if (req.path.startsWith("/api")) {
      let logLine = `${req.method} ${req.path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      if (logLine.length > 80) logLine = logLine.slice(0, 79) + "…";
      log(logLine);
    }
  });
  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Erreur interne du serveur";
    res.status(status).json({ message });
    throw err;
  });

  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  const PORT = parseInt(process.env.PORT || "5000", 10);
  server.listen(PORT, () => {
    console.log(`Serveur démarré sur le port ${PORT}`);
  });
})();

shared/schema.ts.content:
import { z } from "zod";

export type UserRole = "ad" | "shainez" | "pronBOT";

export interface User {
  id: string;
  username: string;
  role: UserRole;
  isOnline: boolean;
  isMuted: boolean;
  mutedUntil?: number;
  isHidden: boolean;
}

export interface Message {
  id: string;
  userId: string;
  username: string;
  role: UserRole;
  content: string;
  timestamp: number;
  status: "pending" | "approved" | "rejected";
  type: "normal" | "event" | "flash";
  forcePublished?: boolean;
  flashDuration?: number;
}

export interface ChatConfig {
  enabled: boolean;
  cooldown: number;
  timerEndTime?: number;
  simulationMode: boolean;
  directChatEnabled: boolean;
}

export interface MutedUser {
  username: string;
  mutedUntil: number;
}

export interface BlockedWord {
  word: string;
  addedAt: number;
}

export interface TypingUser {
  username: string;
  timestamp: number;
}

export const loginSchema = z.object({
  username: z.enum(["ad", "shainez", "pronBOT"]),
  password: z.string().min(1, "Password is required"),
});

export type LoginInput = z.infer<typeof loginSchema>;

export const sendMessageSchema = z.object({
  content: z.string().min(1).max(1000),
});

export type SendMessageInput = z.infer<typeof sendMessageSchema>;

export const eventMessageSchema = z.object({
  content: z.string().min(1).max(500),
});

export const flashMessageSchema = z.object({
  content: z.string().min(1).max(500),
  duration: z.number().min(1).max(60),
});

export const muteUserSchema = z.object({
  username: z.string(),
  duration: z.number().min(1),
});

export const blockWordSchema = z.object({
  word: z.string().min(1),
});

export const chatConfigSchema = z.object({
  enabled: z.boolean().optional(),
  cooldown: z.number().min(0).optional(),
  timerMinutes: z.number().min(0).optional(),
  directChatEnabled: z.boolean().optional(),
});

